package com.dailyAssignments;import java.io.BufferedReader;import java.io.FileReader;import java.io.IOException;import java.util.Arrays;import java.util.List;import java.util.stream.Collectors;public class CountingWhales {    public static long numberOfLines(String path) {        try (BufferedReader reader = new BufferedReader(new FileReader(path))) {            return reader.lines().count();        } catch (IOException e) {            throw new RuntimeException(e);        }    }    public static long numberOfWords(String path) {        return getWords(path).size();    }    public static long numberOfOccurrences(String path, String word) {        List<String> words = getWords(path);        return words.stream().filter(w -> w.equals(word)).count();    }    private static List<String> getWords(String path) {        try (BufferedReader reader = new BufferedReader(new FileReader(path))) {            return reader.lines().flatMap(line -> Arrays.stream(line.split("\\s+"))).collect(Collectors.toList());        } catch (IOException e) {            throw new RuntimeException(e);        }    }}