package com.dailyAssignments;import java.sql.*;import java.util.Properties;public class Main {    public static void main(String[] args) {        String url = "jdbc:mysql://localhost:3306/db";        String username = "db";        String password = "db";        Properties props = new Properties();        props.setProperty("user", "db");        props.setProperty("password", "db");        try (Connection connection = DriverManager.getConnection(url, props)) {            String nameStartWith = "J%";            String query = "SELECT name, department_name, location FROM departments JOIN employees " +                    "ON employees.department_id = departments.department_id  WHERE name LIKE ?";            PreparedStatement preparedStatement = connection.prepareStatement(query);            preparedStatement.setString(1, nameStartWith);            ResultSet resultSet = preparedStatement.executeQuery();            String printableNameStartWith = nameStartWith.replace("%", "");            System.out.printf("Employees name starts with %s \n", printableNameStartWith);            System.out.println("-------------------------");            while (resultSet.next()) {                String name = resultSet.getString("name");                String  departmentName = resultSet.getString("department_name");                String location = resultSet.getString("department_name");                System.out.println(name + " || " + departmentName + " || " + location);            }        } catch (SQLException e) {            throw new RuntimeException(e);        }    }}