package com.dailyAssignments;import java.sql.*;public class Main {    public static void main(String[] args) {        String url = "jdbc:mysql://localhost:3306/temp";        String username = "mariadb";        String password = "mariadb";        try (Connection connection = DriverManager.getConnection(url, username, password)) {            int moreThanPrice = 10;            String query = "SELECT * FROM book WHERE price > ?";            PreparedStatement preparedStatement = connection.prepareStatement(query);            preparedStatement.setInt(1, moreThanPrice);            ResultSet resultSet = preparedStatement.executeQuery();            System.out.printf("Books with price more than %s \n", moreThanPrice);            while (resultSet.next()) {                String title = resultSet.getString("title");                int price = resultSet.getInt("price");                System.out.printf("Title: %s, Price %d \n", title, price);            }        } catch (SQLException e) {            throw new RuntimeException(e);        }    }}